package memotest;public class Memo {	Time time;	boolean notified;	String message;	Notify notify;	public Memo(Time time, String message) {                this.time=time;		this.message=message;		notify = (Memo alarm) -> {			System.out.println("通知事項: " + alarm.message);		};	}	public Memo(Time time, String message, Notify notify) {		this.time = time;		this.message = message;		this.notify = notify;		this.notified = false;	}	public void reset() {		if(this.notified) this.notified = false;	}	@Override	public String toString() {		return "Alarm:" + time + " " + message;	}}