package memotest;import java.util.Date;public class Time {	int hour, min, sec;	public Time(int hh, int mm) {		this(hh, mm, 0);	}	public Time(int hh, int mm, int ss) {		hour = hh;		min = mm;		sec = ss;	}	public boolean timeup(Date now) {		int hh = now.getHours(), mm = now.getMinutes(), ss = now.getSeconds();		if(hh > this.hour) return true;		else if(hh == this.hour) {			if(mm > this.min) return true;			else if(mm == this.min) {				if(ss >= this.sec) return true;			}		}		return false;	}	public String toString() {		return String.format("%02d:%02d:%02d", hour, min, sec);	}}